import { Button } from 'bootstrap';
import './css/Brindamos.css';
import React, {useState} from 'react';
import { Carousel, Card, Row, Col } from 'react-bootstrap';

const Brindamos = () => {
    const [index, setIndex] = useState(0);
  
    const handleSelect = (selectedIndex) => {
      setIndex(selectedIndex);
    };
  
    return (
      <div className="container text-center my-5">
        <h2>Le brindamos...</h2>
  
        <Carousel activeIndex={index} onSelect={handleSelect} className="my-4">
          {/* Slide 1 */}
          <Carousel.Item>
            <div className="row justify-content-center">
              <div className="col-md-5">
                <div className="feature-card">
                  <div className="feature-image" style={{ backgroundImage: "url('ruta-de-la-imagen1.jpg')" }}></div>
                  <h5 className="highlight-title">Calidad Garantizada</h5>
                  <p>El sistema proporciona todas las herramientas necesarias para realizar el trabajo necesario.</p>
                </div>
              </div>
              <div className="col-md-5">
                <div className="feature-card">
                  <div className="feature-image" style={{ backgroundImage: "url('ruta-de-la-imagen2.jpg')" }}></div>
                  <h5 className="highlight-title">Servicio al cliente</h5>
                  <p>Se le brinda un servicio de apoyo y ajustes personalizado al cliente.</p>
                </div>
              </div>
            </div>
          </Carousel.Item>
  
          {/* Slide 2 */}
          <Carousel.Item>
            <div className="row justify-content-center">
              <div className="col-md-5">
                <div className="feature-card">
                  <div className="feature-image" style={{ backgroundImage: "url('ruta-de-la-imagen3.jpg')" }}></div>
                  <h5 className="highlight-title">Soporte Técnico</h5>
                  <p>Reciba soporte técnico 24/7 para solucionar cualquier inconveniente rápidamente.</p>
                </div>
              </div>
              <div className="col-md-5">
                <div className="feature-card">
                  <div className="feature-image" style={{ backgroundImage: "url('ruta-de-la-imagen4.jpg')" }}></div>
                  <h5 className="highlight-title">Actualizaciones Constantes</h5>
                  <p>El sistema se mantiene actualizado con las últimas mejoras y características.</p>
                </div>
              </div>
            </div>
          </Carousel.Item>
        </Carousel>
      </div>
    );
  };

export default Brindamos;
